import React, {Fragment, useState} from 'react';
import ReactTable from "react-table";
import {
    Row, Col,
    Card, CardBody,
    UncontrolledButtonDropdown, DropdownItem, DropdownMenu, DropdownToggle
} from 'reactstrap';
import avatar2 from "../../assets/utils/images/avatars/2.jpg";
import {makeData} from "../Tables/DataTables/Examples/utils"

class ProductTable extends React.Component {
    constructor() {
        super();
        this.state = {
            productData: makeData()
        };
    }

    render() {
        const {productData} = this.state;
        return (
            <Fragment>
                <Card className="main-card mb-3">
                    <CardBody>
                        <ReactTable
                            data={productData}
                            columns={[{
                                columns: [
                                    {
                                        Header: 'Book',
                                        accessor: 'lastName',
                                        // Cell: row => (
                                        //     <div>
                                        //         <div className="widget-content p-0">
                                        //             <div className="widget-content-wrapper">
                                        //                 <div className="widget-content-left mr-3">
                                        //                     <div className="widget-content-left">
                                        //                         <img width={52} className="rounded-circle"
                                        //                              src={avatar2}
                                        //                              alt=""/>
                                        //                     </div>
                                        //                 </div>
                                        //                 <div className="widget-content-left flex2">
                                        //                     <div className="widget-heading">
                                        //                         {row.value}
                                        //                     </div>
                                        //                     <div className="widget-subheading opacity-10">
                                        //                                     <span className="pr-2">
                                        //                                         <b className="text-danger">12</b> Leads
                                        //                                     </span>
                                        //                         <span>
                                        //                                         <b className="text-success">$56,24</b> Totals
                                        //                                     </span>
                                        //                     </div>
                                        //                 </div>
                                        //             </div>
                                        //         </div>
                                        //     </div>
                                        // )
                                    },
                                    {
                                        Header: 'Stock',
                                        accessor: 'age'
                                    }
                                    // {
                                    //     Header: 'Visits',
                                    //     accessor: 'visits'
                                    // },
                                    // {
                                    //     Header: 'Popular Tag',
                                    //     accessor: 'lastName'
                                    // },
                                ]
                            },
                                {
                                    columns: [

                                        {
                                            Header: 'Actions',
                                            accessor: 'actions',
                                            Cell: row => (
                                                <div className="d-block w-100 text-center">
                                                    <UncontrolledButtonDropdown>
                                                        <DropdownToggle caret
                                                                        className="btn-icon btn-icon-only btn btn-link"
                                                                        color="link">
                                                            <i className="lnr-menu-circle btn-icon-wrapper"/>
                                                        </DropdownToggle>
                                                        <DropdownMenu className="rm-pointers dropdown-menu-hover-link">
                                                            <DropdownItem header>Header</DropdownItem>
                                                            <DropdownItem>
                                                                <i className="dropdown-icon lnr-inbox"> </i>
                                                                <span>Menus</span>
                                                            </DropdownItem>
                                                            <DropdownItem>
                                                                <i className="dropdown-icon lnr-file-empty"> </i>
                                                                <span>Settings</span>
                                                            </DropdownItem>
                                                            <DropdownItem>
                                                                <i className="dropdown-icon lnr-book"> </i>
                                                                <span>Actions</span>
                                                            </DropdownItem>
                                                            <DropdownItem divider/>
                                                            <DropdownItem>
                                                                <i className="dropdown-icon lnr-picture"> </i>
                                                                <span>Dividers</span>
                                                            </DropdownItem>
                                                        </DropdownMenu>
                                                    </UncontrolledButtonDropdown>
                                                </div>
                                            )
                                        }
                                    ]
                                }]}
                            defaultPageSize={10}
                            className="-striped -highlight"
                        />
                    </CardBody>
                </Card>
            </Fragment>
        )
    }
}

export default ProductTable;
