import React, {Fragment} from 'react';

import {
    UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem, Popover,
    Nav, NavLink, Col, Row, NavItem, UncontrolledButtonDropdown, Button
} from 'reactstrap';

import PerfectScrollbar from 'react-perfect-scrollbar';

import bg2 from '../../../assets/utils/images/dropdown-header/abstract2.jpg';
import bg3 from '../../../assets/utils/images/dropdown-header/abstract3.jpg';

import {
    faAngleDown,

} from '@fortawesome/free-solid-svg-icons';

import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';

class MegaMenu extends React.Component {
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            dropdownOpen: false,
            popoverOpen: false,
        };
    }

    toggle() {
        this.setState({
            dropdownOpen: !this.state.dropdownOpen,
            popoverOpen: !this.state.popoverOpen

        });
    }

    state = {};

    render() {
        return (
            <Fragment>
                <Nav className="header-megamenu">
                    {/*<NavItem>*/}
                    {/*    <NavLink href="#" onClick={this.toggle} id="PopoverMegaMenu">*/}
                    {/*        <i className="nav-link-icon pe-7s-gift"> </i>*/}
                    {/*        Mega Menu*/}
                    {/*        <FontAwesomeIcon className="ml-2 opacity-5" icon={faAngleDown}/>*/}
                    {/*    </NavLink>*/}
                    {/*</NavItem>*/}
                    {/*<Popover className="rm-max-width" placement="bottom-start" fade={false} trigger="legacy" isOpen={this.state.popoverOpen} target="PopoverMegaMenu"*/}
                    {/*         toggle={this.toggle}>*/}
                    {/*    <div className="dropdown-mega-menu">*/}
                    {/*        <div className="grid-menu grid-menu-3col">*/}
                    {/*            <Row className="no-gutters">*/}
                    {/*                <Col xl="4" sm="6">*/}
                    {/*                    <Nav vertical>*/}
                    {/*                        <NavItem className="nav-item-header">*/}
                    {/*                            Overview*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">*/}
                    {/*                                <i className="nav-link-icon lnr-inbox"> </i>*/}
                    {/*                                <span>Contacts</span>*/}
                    {/*                            </NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">*/}
                    {/*                                <i className="nav-link-icon lnr-book"> </i>*/}
                    {/*                                <span>Incidents</span>*/}
                    {/*                                <div className="ml-auto badge badge-pill badge-danger">5</div>*/}
                    {/*                            </NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">*/}
                    {/*                                <i className="nav-link-icon lnr-picture"> </i>*/}
                    {/*                                <span>Companies</span>*/}
                    {/*                            </NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink disabled href="#">*/}
                    {/*                                <i className="nav-link-icon lnr-file-empty"> </i>*/}
                    {/*                                <span>Dashboards</span>*/}
                    {/*                            </NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                    </Nav>*/}
                    {/*                </Col>*/}
                    {/*                <Col xl="4" sm="6">*/}
                    {/*                    <Nav vertical>*/}
                    {/*                        <NavItem className="nav-item-header">*/}
                    {/*                            Favourites*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">Reports Conversions</NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">*/}
                    {/*                                Quick Start*/}
                    {/*                                <div className="ml-auto badge badge-success">New</div>*/}
                    {/*                            </NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">*/}
                    {/*                                Users &amp; Groups*/}
                    {/*                            </NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">*/}
                    {/*                                Proprieties*/}
                    {/*                            </NavLink>*/}
                    {/*                        </NavItem>*/}
                    {/*                    </Nav>*/}
                    {/*                </Col>*/}
                    {/*                <Col xl="4" sm="6">*/}
                    {/*                    <Nav vertical>*/}
                    {/*                        <NavItem className="nav-item-header">*/}
                    {/*                            Sales &amp; Marketing*/}
                    {/*                        </NavItem>*/}
                    {/*                        <NavItem>*/}
                    {/*                            <NavLink href="#">*/}
                    {/*                                Queues*/}
